import React from 'react';
import './singin.scss';
import FormInput from '../../components/singin/singin';
class SignIn extends React.Component {
    constructor(props){
        super(props);


        this.state = {
            email:'',
            password:''
        }
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.setState({email:'', password:''})
    }

    handleChange = (e) => {
        const {value, name} = e.target;

        this.setState({[name]:value})
    }

    render(){
        return(
            <div className="sign-in">
                <h2>I alredy have an account</h2>
                <span>Sing in with your email and password</span>

                <form onSubmit={this.handleSubmit}>
                    <FormInput name="emale" type = "email" value={this.state.emale} handleChange={this.handleChange} requered></FormInput>
                    <label>Email</label>
                    <FormInput name="password" type = "password" value={this.state.password} handleChange={this.handleChange}  requered></FormInput>
                    <label>Password</label>

                    <input type="summit" value = "Submit Form"></input>
                </form>
            </div>
        )
    }
}

export default SignIn;
